USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

SELECT date_trunc('month', order_date)::DATE AS order_dates,
  SUM(price) AS total_sales
FROM workspace.salesdb.dim_products
JOIN workspace.salesdb.fact_sales ON
  dim_products.product_key = fact_sales.product_key
GROUP BY 1
ORDER BY 1;

USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

--This query for show total quantity as sold each month by not include order_Date if null
SELECT date_trunc('month', order_date)::DATE AS order_dates,
  SUM(quantity) AS Total_quantity
FROM fact_sales
WHERE order_date IS NOT NULL
GROUP BY 1
ORDER BY 1;

USE CATALOG `workspace`;
USE SCHEMA `default`;

--This query wanna show total_Sales by category
SELECT category,
  SUM(sales_amount) AS total_Sales_by_category
FROM workspace.salesdb.fact_sales
JOIN workspace.salesdb.dim_products ON  
  dim_products.product_key = fact_sales.product_key
GROUP BY category
ORDER BY 2 DESC;

USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

--This Query to show total sold all 
SELECT SUM(price) AS total_sales
