USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

--This query to show Total number of orders
SELECT COUNT(DISTINCT order_number)
FROM workspace.salesdb.fact_sales;

USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

--This query to show avg_sales_per_customer
SELECT CONCAT('$ ', ROUND(SUM(sales_amount)/COUNT(DISTINCT customer_key),1)) AS avg_sales
FROM workspace.salesdb.fact_sales;

USE CATALOG `workspace`;
USE SCHEMA `salesdb`;

--This query to show total number of customer
SELECT COUNT(customer_id) AS total_numbers_of_customer
FROM workspace.salesdb.dim_customers;

USE CATALOG `workspace`;
USE SCHEMA `default`;

--This query is show country product sales 
SELECT country,
  SUM(sales_amount) AS total_sales
FROM workspace.salesdb.dim_customers
JOIN workspace.salesdb.fact_sales ON
  workspace.salesdb.fact_sales.customer_key = workspace.salesdb.dim_customers.customer_key
WHERE country != 'n/a'
GROUP BY 1;
